mapper.select:
SELECT EMAIL FROM TB_TWITTER_AUTH_TOKEN
WHERE 1=1
AND EMAIL = #{email}

mapper.update:
UPDATE TB_TWITTER_AUTH_TOKEN
SET USE_DATE = #{useDate}
WHERE 1=1 AND USER_ID = #{userId}

mapper.select:
SELECT * FROM TB_TWITTER_AUTH_TOKEN
WHERE 1=1
AND STATUS = #{status}

SELECT * FROM TB_TWITTER_AUTH_TOKEN
WHERE 1=1
AND IP = #{ip}
AND STATUS = #{status}
